import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import davanuImg from './assets/cover.png';

const DavanuKartes = () => {
  const [active, setActive] = useState('veikals');

  const kartes = {
    veikals: {
      title: 'Veikala dāvanu karte',
      text: `Dāvanu karte rotu izvēlei. Karti saņēmējs varēs izmantot gan mūsu veikalos Vecrīgā un Siguldā, gan internetā. Kartes var saņemt gan ar piegādi, gan elektroniski e-pastā.`,
      img: davanuImg
    },
    namejs: {
      title: 'Nameja meistarklases dāvanu karte',
      text: `Piedalies meistarklasē un izgatavo savu Nameja gredzenu – unikāla dāvana ar stāstu.`,
      img: davanuImg
    },
    nospiedums: {
      title: 'Nospiedumu meistarklases dāvanu karte',
      text: `Veido kulonu ar savu pirkstu nospiedumu – simboliska un personiska dāvana īpašam cilvēkam.`,
      img: davanuImg
    }
  };

  const current = kartes[active];

  return (
    <div className="max-w-7xl mx-auto px-4 py-16">
      <h1 className="text-4xl font-bold mb-10">Dāvanu kartes</h1>
      <div className="grid md:grid-cols-3 gap-8 items-start">
        
        {/* Izvēlne kreisajā pusē */}
        <div className="space-y-4 border-l pl-4 text-lg font-medium text-[#1c203c]">
          <button onClick={() => setActive('veikals')} className="hover:underline block">Veikala dāvanu karte</button>
          <button onClick={() => setActive('namejs')} className="hover:underline block">Nameja meistarklases dāvanu karte</button>
          <button onClick={() => setActive('nospiedums')} className="hover:underline block">Nospiedumu meistarklases dāvanu karte</button>
        </div>

        {/* Saturs ar animāciju */}
        <div className="md:col-span-2">
          <AnimatePresence mode="wait">
            <motion.div
              key={active}
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.4 }}
              className="grid md:grid-cols-2 gap-8 items-center"
            >
              <div>
                <h2 className="text-3xl font-bold mb-4">{current.title}</h2>
                <p className="text-gray-700 text-base mb-6 leading-relaxed whitespace-pre-line">
                  {current.text}
                </p>
                <a
                  href="mailto:info@balturotas.lv?subject=Pasūtīt dāvanu karti"
                  className="inline-block text-xl font-semibold underline underline-offset-4 text-[#1c203c] hover:text-blue-800 transition"
                >
                  Pasūtīt →
                </a>
              </div>
              <img
                src={current.img}
                alt={current.title}
                className="w-full rounded-lg shadow-md object-cover"
              />
            </motion.div>
          </AnimatePresence>
        </div>
      </div>
    </div>
  );
};

export default DavanuKartes;
