import React, { useState } from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import ProductList from './ProductList';
import Cart from './Cart';

const App = () => {
  const [cartItems, setCartItems] = useState([]);

  const addToCart = (product) => {
    setCartItems((prev) => [...prev, product]);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-white shadow-md">
        <div className="max-w-6xl mx-auto flex justify-between items-center p-4">
          <div className="flex items-center gap-2">
            <img src="https://balturotas.lv/logo.svg" alt="Baltu Rotas" className="h-10" />
            <span className="text-xl font-bold tracking-wide">Baltu Rotas</span>
          </div>
          <nav className="hidden sm:flex gap-6 text-sm font-medium">
            <a href="#" className="hover:text-blue-600">SƒÅkums</a>
            <a href="#" className="hover:text-blue-600">Veikals</a>
            <a href="#" className="hover:text-blue-600">Kontakti</a>
          </nav>
          <div className="text-sm text-gray-600">üõí {cartItems.length} prece(s)</div>
        </div>
      </header>
      <ProductList onAddToCart={addToCart} />
      <Cart items={cartItems} />
    </div>
  );
};

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<App />);
