import React, { useState } from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import ProductList from './ProductList';
import Cart from './Cart';
import heroImg from './assets/hero.png';

const App = () => {
  const [cartItems, setCartItems] = useState([]);

  const addToCart = (product) => {
    setCartItems((prev) => [...prev, product]);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-white shadow-md sticky top-0 z-50">
        <div className="max-w-6xl mx-auto flex justify-between items-center p-4">
          <div className="flex items-center gap-2">
            <img src="https://balturotas.lv/logo.svg" alt="Baltu Rotas" className="h-10" />
            <span className="text-xl font-bold tracking-wide">Baltu Rotas</span>
          </div>
          <nav className="hidden sm:flex gap-6 text-sm font-medium">
            <a href="#sakums" className="hover:text-blue-600">Sākums</a>
            <a href="#veikals" className="hover:text-blue-600">Veikals</a>
            <a href="#kontakti" className="hover:text-blue-600">Kontakti</a>
          </nav>
          <div className="text-sm text-gray-600">🛒 {cartItems.length} prece(s)</div>
        </div>
      </header>

      <section
        id="sakums"
        className="h-screen bg-cover bg-center flex items-center justify-center text-center text-white px-4"
        style={{ backgroundImage: `url(${heroImg})` }}
      >
        <h1 className="text-2xl sm:text-3xl md:text-4xl max-w-2xl leading-relaxed font-medium">
          Nest savas saknes kā rotu. Katrā rotā dzīvo kāds stāsts. Par zemē un tautā sakņotu dzīves gudrību.
          Par dabas nepieradinātību. Par stiprās dzimtās kaltinātu briedumu un drosmi.
        </h1>
      </section>

      <section id="veikals" className="bg-gray-50 py-10">
        <ProductList onAddToCart={addToCart} />
      </section>

      <section id="kontakti" className="bg-white py-10">
        <div className="max-w-3xl mx-auto px-4">
          <h2 className="text-2xl font-bold mb-4">Kontakti</h2>
          <p className="text-gray-700 mb-2">E-pasts: info@balturotas.lv</p>
          <p className="text-gray-700">Tālrunis: +371 12345678</p>
        </div>
      </section>

      <Cart items={cartItems} />
    </div>
  );
};

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<App />);
