import React from 'react';
import davanuImg from './assets/cover.png'; // pieliec savu attēlu šeit

const DavanuKartes = () => {
  return (
    <div>
      {/* HERO sadaļa */}
      <section
        className="h-[60vh] bg-cover bg-center flex items-center justify-center text-center"
        style={{ backgroundImage: `url(${davanuImg})` }}
      >
        <h1 className="text-white text-3xl sm:text-4xl md:text-5xl font-medium px-4">
          Dāvini stāstu rotas veidolā
        </h1>
      </section>

      {/* Saturs */}
      <section className="bg-white py-16 px-6">
        <div className="max-w-7xl mx-auto grid md:grid-cols-2 gap-10 items-center">

          {/* Teksts pa kreisi */}
          <div>
            <h2 className="text-3xl font-bold mb-6">Dāvanu kartes</h2>
            <p className="text-gray-700 text-lg leading-relaxed mb-4">
              Dāvanu karte ir ideāla iespēja kādam īpašam uzdāvināt brīvību – iespēju pašam izvēlēties rotu, kas atbilst viņa gaumei, vērtībām un stāstam.
            </p>
            <p className="text-gray-700 text-lg leading-relaxed mb-4">
              Kartes iespējams iegādāties fiziski vai digitāli, izvēloties jebkādu summu. Tās var izmantot gan mūsu veikalos, gan arī internetā.
            </p>
            <p className="text-gray-700 text-lg leading-relaxed mb-4">
              Lai saņemtu karti, sazinieties ar mums – mēs to nosūtīsim Jums vai saņēmējam pa pastu vai e-pastu.
            </p>
          </div>

          {/* Attēls pa labi */}
          <div>
            <img
              src={davanuImg}
              alt="Dāvanu karte"
              className="w-full rounded-xl shadow-lg object-cover"
            />
          </div>
        </div>
      </section>
    </div>
  );
};

export default DavanuKartes;
