import React from 'react';
import { useParams } from 'react-router-dom';

const products = [
  { id: 1, type: 'Gredzeni', material: 'Zelts', name: 'Lielvārdes jostas motīvs', price: 430, image: 'https://picsum.photos/seed/1/800', description: 'Dzeltanā zelta 585 proves/14K gredzens ar Lielvārdes jostas motīvu.', size: '4mm', code: 'Z1.168.15' },
  { id: 2, type: 'Gredzeni', material: 'Zelts', name: 'Lielvārdes jostas motīvs (plats)', price: 729, image: 'https://picsum.photos/seed/2/800', description: 'Platāka versija ar Lielvārdes jostas simboliem.', size: '6mm', code: 'Z1.168.16' },
];

const ProductDetails = () => {
  const { id } = useParams();
  const product = products.find((p) => p.id === Number(id));

  if (!product) return <div className="p-6 text-center">Produkts nav atrasts.</div>;

  return (
    <div className="max-w-6xl mx-auto p-6 grid md:grid-cols-2 gap-6">
      <div>
        <img src={product.image} alt={product.name} className="w-full rounded" />
      </div>
      <div className="space-y-4">
        <h1 className="text-2xl font-bold">{product.name}</h1>
        <p className="text-sm text-gray-500">Rotas apraksts</p>
        <p>{product.description}</p>
        <p className="text-sm text-gray-500">Papildus informācija</p>
        <p>{product.size}</p>
        <p className="text-sm text-gray-500">Metāls</p>
        <p>{product.material}</p>
        <p className="text-sm text-gray-500">Kods</p>
        <p>{product.code}</p>
        <div className="flex items-center gap-4 mt-4">
          <span className="text-xl font-semibold">{product.price.toFixed(2)} €</span>
          <button className="bg-blue-900 text-white px-6 py-2 rounded hover:bg-blue-800">Pievienot grozam</button>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
